"use client"
import React, { useState, useEffect, useRef } from 'react';

// Icons for the component
import { Plus, ChevronDown, Mic, ArrowUp, Image as ImageIcon, Box, FileText, Brain } from 'lucide-react';

const PromptComponent = () => {
  const [prompt, setPrompt] = useState('');

  // State to manage the visibility of popups and dropdowns
  const [isAddPopupOpen, setAddPopupOpen] = useState(false);
  const [isModelOpen, setModelOpen] = useState(false);

  // State for the selected model
  const [selectedModel, setSelectedModel] = useState('Brainwave 2.5');
  const models = ['Brainwave 2.5', 'Creative Fusion', 'Visionary AI 3.0'];

  // Refs for the popups to detect outside clicks
  const addPopupRef = useRef<HTMLDivElement>(null);
  const modelRef = useRef<HTMLDivElement>(null);
  
  // Effect to handle clicks outside of the popups/dropdowns
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (addPopupRef.current && !addPopupRef.current.contains(event.target as Node)) {
        setAddPopupOpen(false);
      }
      if (modelRef.current && !modelRef.current.contains(event.target as Node)) {
        setModelOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleModelSelect = (model: string) => {
    setSelectedModel(model);
    setModelOpen(false);
  };
  
  const handleUpload = () => {
    if(!prompt.trim()) {
        // Using console.error instead of alert
        console.error("Please describe your 3D object or scene first!");
        return;
    }
    console.log(`Uploading prompt: "${prompt}" with model: ${selectedModel}`);
    // Add your upload logic here
  }

  const addMenuItems = [
    { icon: <ImageIcon size={20} className="text-gray-500 dark:text-gray-400" />, text: "Add photos or videos" },
    { icon: <Box size={20} className="text-gray-500 dark:text-gray-400" />, text: "Add 3D objects" },
    { icon: <FileText size={20} className="text-gray-500 dark:text-gray-400" />, text: "Add files (docs, txt...)" },
  ];

  return (
    <div className="w-full max-w-2xl p-4">
      <div className="bg-white/80 backdrop-blur-xl dark:bg-black/90 rounded-3xl shadow-2xl border border-gray-200/50 dark:border-gray-800/50 p-6 transition-all duration-300 hover:shadow-3xl">
        <textarea
          className="w-full p-3 bg-transparent text-gray-800 dark:text-gray-100 placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none resize-none text-base font-medium leading-relaxed"
          rows={2}
          placeholder="Describe your 3D object or scene..."
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
        />
        {/* Responsive container for controls */}
        <div className="flex flex-col md:flex-row items-center justify-between mt-4 gap-4 md:gap-0">
          {/* Left side controls */}
          <div className="flex flex-wrap items-center gap-3">
            {/* Add Button and Popup */}
            <div className="relative" ref={addPopupRef}>
              <button 
                onClick={() => setAddPopupOpen(!isAddPopupOpen)}
                className="flex items-center justify-center w-12 h-12 bg-gradient-to-br from-gray-50 to-gray-100 dark:from-gray-900 dark:to-gray-800 hover:from-gray-100 hover:to-gray-200 dark:hover:from-gray-800 dark:hover:to-gray-700 text-gray-600 dark:text-gray-300 rounded-2xl transition-all duration-200 shadow-lg hover:shadow-xl border border-gray-200/50 dark:border-gray-700/50"
              >
                <Plus size={22} />
              </button>
              {isAddPopupOpen && (
                <div className="absolute bottom-full left-0 mb-3 w-72 bg-white/95 backdrop-blur-xl dark:bg-gray-900/95 rounded-2xl shadow-2xl border border-gray-200/50 dark:border-gray-700/50 z-10">
                  <ul>
                    {addMenuItems.map((item, index) => (
                       <li key={index} className="flex items-center gap-4 p-4 hover:bg-gray-50/80 dark:hover:bg-gray-800/80 cursor-pointer rounded-xl transition-colors duration-200 first:rounded-t-2xl last:rounded-b-2xl">
                         {item.icon}
                         <span className="font-medium text-gray-700 dark:text-gray-200">{item.text}</span>
                       </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
            
            {/* Model Selection Button and Dropdown */}
            <div className="relative" ref={modelRef}>
              <button onClick={() => setModelOpen(!isModelOpen)} className="flex items-center justify-center h-12 px-4 lg:px-5 bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-blue-900/30 dark:to-indigo-800/30 hover:from-blue-100 hover:to-indigo-200 dark:hover:from-blue-800/40 dark:hover:to-indigo-700/40 text-gray-800 dark:text-gray-200 rounded-2xl transition-all duration-200 shadow-lg hover:shadow-xl border border-blue-200/50 dark:border-blue-700/30">
                <Brain size={18} className="text-blue-600 dark:text-blue-400" />
                <span className="font-semibold ml-2 hidden lg:block">{selectedModel}</span>
                <ChevronDown size={16} className="ml-2 hidden lg:block" />
              </button>
               {isModelOpen && (
                 <div className="absolute bottom-full left-0 mb-3 w-64 bg-white/95 backdrop-blur-xl dark:bg-gray-900/95 rounded-2xl shadow-2xl border border-gray-200/50 dark:border-gray-700/50 z-10">
                  <ul>
                    {models.map((model) => (
                       <li key={model} onClick={() => handleModelSelect(model)} className="p-4 hover:bg-gray-50/80 dark:hover:bg-gray-800/80 cursor-pointer font-medium text-gray-700 dark:text-gray-200 rounded-xl transition-colors duration-200 first:rounded-t-2xl last:rounded-b-2xl">
                         {model}
                       </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>
          {/* Right side controls */}
          <div className="flex items-center gap-3">
            <button onClick={() => console.log("Mic clicked")} className="flex items-center justify-center w-12 h-12 bg-gradient-to-br from-gray-50 to-gray-100 dark:from-gray-900 dark:to-gray-800 hover:from-gray-100 hover:to-gray-200 dark:hover:from-gray-800 dark:hover:to-gray-700 text-gray-600 dark:text-gray-300 rounded-full transition-all duration-200 shadow-lg hover:shadow-xl border border-gray-200/50 dark:border-gray-700/50">
                <Mic size={22} />
            </button>
            <button onClick={handleUpload} className={`flex items-center justify-center w-12 h-12 rounded-2xl transition-all duration-200 shadow-lg hover:shadow-xl ${
              prompt.trim() 
                ? 'bg-gradient-to-br from-gray-800 to-gray-900 hover:from-gray-900 hover:to-black dark:from-blue-600 dark:to-blue-700 dark:hover:from-blue-500 dark:hover:to-blue-600 text-white' 
                : 'bg-gradient-to-br from-gray-200 to-gray-300 dark:from-gray-800 dark:to-gray-700 text-gray-400 dark:text-gray-500 cursor-not-allowed'
            }`}>
              <ArrowUp size={22} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PromptComponent;

"use client"
import { useState, useRef, useEffect, useCallback, useMemo } from 'react';

// Utility function to join class names (replaces `cn`)
const classNames = (...classes: (string | undefined | null | false)[]): string => {
  return classes.filter(Boolean).join(' ');
};

// Custom hook for auto-resizing textarea (replaces `useAutoResizeTextarea`)
const useAutoResizeTextarea = ({ minHeight, maxHeight }: { minHeight?: number; maxHeight?: number }) => {
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  const adjustHeight = useCallback((forceShrink = false) => {
    if (textareaRef.current) {
      // Temporarily set height to auto to calculate scrollHeight correctly
      textareaRef.current.style.height = 'auto';
      let newHeight = textareaRef.current.scrollHeight;

      // Apply min and max height constraints
      if (minHeight && newHeight < minHeight) {
        newHeight = minHeight;
      }
      if (maxHeight && newHeight > maxHeight) {
        newHeight = maxHeight;
      }

      // If forceShrink is true, reset to minHeight
      if (forceShrink && minHeight) {
        newHeight = minHeight;
      }

      textareaRef.current.style.height = `${newHeight}px`;
    }
  }, [minHeight, maxHeight]);

  // Adjust height on initial render and when value changes
  useEffect(() => {
    adjustHeight();
  }, [adjustHeight]); // Include adjustHeight in dependencies

  return { textareaRef, adjustHeight };
};

// Inline SVG Icons (replaces lucide-react components)
const Paperclip = (props: React.SVGProps<SVGSVGElement>) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    {...props}
  >
    <path d="m21.44 11.05-9.19 9.19a6 6 0 0 1-8.49-8.49l9.19-9.19a4 4 0 0 1 5.66 5.66l-9.2 9.19a2 2 0 0 1-2.83-2.83l8.49-8.48" />
  </svg>
);

const PlusIcon = (props: React.SVGProps<SVGSVGElement>) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    {...props}
  >
    <path d="M12 5v14" />
    <path d="M5 12h14" />
  </svg>
);

const ArrowUpIcon = (props: React.SVGProps<SVGSVGElement>) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    {...props}
  >
    <path d="m5 12 7-7 7 7" />
    <path d="M12 19V5" />
  </svg>
);

// Recreated Button component
interface ButtonProps {
  children: React.ReactNode;
  className?: string;
  type?: 'button' | 'submit' | 'reset';
  onClick?: () => void;
  variant?: 'default' | 'outline' | 'secondary' | 'ghost' | 'link' | 'destructive';
  disabled?: boolean;
  size?: string; // Adding size prop to fix the error
}

const Button = ({ children, className, type = 'button', onClick, variant = 'default', disabled = false }: ButtonProps) => {
  const baseClasses = 'inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50';
  let variantClasses = '';

  switch (variant) {
    case 'outline':
      variantClasses = 'border border-gray-200 bg-white hover:bg-gray-100 hover:text-gray-900 dark:border-gray-700 dark:bg-black dark:hover:bg-gray-900 dark:hover:text-white dark:text-gray-100';
      break;
    case 'secondary':
      variantClasses = 'bg-gray-100 text-gray-900 hover:bg-gray-200 dark:bg-gray-800 dark:text-gray-100 dark:hover:bg-gray-700';
      break;
    case 'ghost':
      variantClasses = 'hover:bg-gray-100 hover:text-gray-900 dark:hover:bg-gray-800 dark:hover:text-gray-100 dark:text-gray-300';
      break;
    case 'link':
      variantClasses = 'text-blue-600 underline-offset-4 hover:underline dark:text-blue-400';
      break;
    case 'destructive':
      variantClasses = 'bg-red-600 text-white hover:bg-red-700 dark:bg-red-700 dark:hover:bg-red-800';
      break;
    default: // 'default'
      variantClasses = 'bg-black text-white hover:bg-gray-800 dark:bg-white dark:text-black dark:hover:bg-gray-200';
      break;
  }

  return (
    <button type={type} className={classNames(baseClasses, variantClasses, className)} onClick={onClick} disabled={disabled}>
      {children}
    </button>
  );
};

export function VercelV0Chat() {
  const [value, setValue] = useState('');
  const [selectedModel, setSelectedModel] = useState('GPT-4'); // State for selected model
  const { textareaRef, adjustHeight } = useAutoResizeTextarea({
    minHeight: 60,
    maxHeight: 200,
  });

  // Placeholder animation state
  const placeholderTexts = useMemo(() => [
    "Ask v0 a question...",
    "Generate a component...",
    "Create a new page...",
    "Design a landing page...",
    "Build a full stack app..."
  ], []);

  const [displayedPlaceholder, setDisplayedPlaceholder] = useState('');
  const [showCursor, setShowCursor] = useState(true); // For blinking cursor
  const animationState = useRef<{
    currentTextIndex: number;
    charIndex: number;
    isDeleting: boolean;
    timeoutId: NodeJS.Timeout | null;
  }>({
    currentTextIndex: 0,
    charIndex: 0,
    isDeleting: false,
    timeoutId: null,
  });

  const typingSpeed = 70;
  const deletingSpeed = 40;
  const pauseBeforeDelete = 1500;
  const pauseBeforeType = 500;

  // Animation logic
  useEffect(() => {
    const state = animationState.current;

    const animate = () => {
      const fullText = placeholderTexts[state.currentTextIndex];

      if (!state.isDeleting) {
        // Typing phase
        if (state.charIndex < fullText.length) {
          setDisplayedPlaceholder(fullText.substring(0, state.charIndex + 1));
          state.charIndex++;
          state.timeoutId = setTimeout(animate, typingSpeed);
        } else {
          // Finished typing
          state.isDeleting = true;
          state.timeoutId = setTimeout(animate, pauseBeforeDelete);
        }
      } else {
        // Deleting phase
        if (state.charIndex > 0) {
          setDisplayedPlaceholder(fullText.substring(0, state.charIndex - 1));
          state.charIndex--;
          state.timeoutId = setTimeout(animate, deletingSpeed);
        } else {
          // Finished deleting
          state.isDeleting = false;
          state.currentTextIndex = (state.currentTextIndex + 1) % placeholderTexts.length;
          state.charIndex = 0; // Reset charIndex for next phrase
          state.timeoutId = setTimeout(animate, pauseBeforeType);
        }
      }
    };

    // Start the animation loop
    state.timeoutId = setTimeout(animate, pauseBeforeType);

    // Cursor blinking effect
    const cursorInterval = setInterval(() => {
      setShowCursor(prev => !prev);
    }, 500); // Blinks every 500ms

    return () => {
      if (state.timeoutId) {
        clearTimeout(state.timeoutId);
      }
      clearInterval(cursorInterval);
    };
  }, [placeholderTexts]); // Include placeholderTexts in dependencies

  const handleSend = () => {
    if (value.trim()) {
      console.log("Sending message:", value.trim());
      console.log("Using model:", selectedModel); // Log the selected model
      setValue('');
      adjustHeight(true); // Force shrink after sending
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  // Adjust height on value change
  useEffect(() => {
    adjustHeight();
  }, [value, adjustHeight]);

  return (
    <div className={classNames(
      "mx-auto flex w-full max-w-4xl flex-col items-center space-y-4 p-4 py-24 sm:space-y-8 font-sans",
    )}>
      {/* Note: For production, add Inter font to _document.js or use a proper font loading strategy */}

      <h1 className="text-center text-2xl font-bold sm:text-4xl text-gray-900 dark:text-white">
        What can I help you ship?
      </h1>

      <div className="w-full flex flex-col items-center space-y-4">
        {/* Main Input area */}
        <div className="relative w-full rounded-xl border border-gray-200 bg-gray-50/20 shadow-lg dark:border-gray-700 dark:bg-gray-900/20 dark:shadow-gray-900/20">
          <div className="overflow-y-auto">
            <textarea
              ref={textareaRef}
              value={value}
              onChange={(e) => {
                setValue(e.target.value);
              }}
              onKeyDown={handleKeyDown}
              placeholder={displayedPlaceholder + (showCursor && value.length === 0 ? '|' : '')} // Blinking cursor only when input is empty
              className={classNames(
                'w-full px-4 py-3',
                'resize-none',
                'bg-transparent',
                'border-none',
                'text-sm',
                'focus:outline-none',
                'focus-visible:ring-0 focus-visible:ring-offset-0',
                'placeholder:text-sm placeholder:text-gray-500 dark:placeholder:text-gray-400',
                'text-gray-900 dark:text-white',
                'min-h-[60px]',
                'rounded-xl'
              )}
              style={{
                overflow: 'hidden',
              }}
            />
          </div>

          <div className="flex items-center justify-between p-3 border-t border-gray-200 dark:border-gray-700">
            <div className="flex items-center gap-2">
              <Button
                type="button"
                size="sm"
                variant="outline"
                className="group flex items-center gap-1 rounded-lg p-2"
              >
                <Paperclip className="h-4 w-4" />
                <span className="hidden text-xs transition-opacity group-hover:inline">
                  Attach
                </span>
              </Button>

              {/* Model Selection Dropdown */}
              <select
                id="model-select"
                value={selectedModel}
                onChange={(e) => setSelectedModel(e.target.value)}
                className={classNames(
                  'px-3 py-2 rounded-md border border-gray-300 bg-white text-sm text-gray-900',
                  'dark:border-gray-600 dark:bg-gray-800 dark:text-gray-100',
                  'focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500',
                  'dark:focus:ring-blue-400 dark:focus:border-blue-400'
                )}
              >
                <option value="GPT-4">GPT-4</option>
                <option value="DeepSak-R1">DeepSak-R1</option>
                <option value="Gemini Pro">Gemini Pro</option>
                <option value="Claude 3 Opus">Claude 3 Opus</option>
                <option value="Llama 3">Llama 3</option>
              </select>
            </div>

            <div className="flex items-center gap-2">
              <Button
                type="button"
                size="sm"
                variant="secondary"
                className="flex items-center justify-between gap-1 rounded-lg border border-dashed px-2 py-1 text-sm border-gray-300 dark:border-gray-600"
              >
                <PlusIcon className="h-4 w-4" />
                Project
              </Button>
              <Button
                type="button"
                onClick={handleSend}
                disabled={!value.trim()}
                className={classNames(
                  'flex items-center justify-between gap-1 rounded-lg px-2.5 py-2.5 text-sm',
                  value.trim()
                    ? 'bg-black text-white dark:bg-white dark:text-black'
                    : 'bg-gray-100 text-gray-400 dark:bg-gray-800 dark:text-gray-500'
                )}
              >
                <ArrowUpIcon
                  className={classNames(
                    'h-4 w-4',
                    value.trim()
                      ? 'text-white dark:text-black'
                      : 'text-gray-400 dark:text-gray-500'
                  )}
                />
                <span className="sr-only">Send</span>
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default VercelV0Chat;
