                  onClick={togglePasswordVisibility}
                  className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-400 dark:text-gray-500 hover:text-gray-600 dark:hover:text-gray-300 transition-colors"
                >
                  {showPassword ? <EyeOffIcon /> : <EyeIcon />}
                </button>
              </div>
            </div>

            {/* Terms Checkbox */}
            <div className="flex items-start space-x-2">
              <div className="relative flex items-center">
                <input
                  id="terms"
                  type="checkbox"
                  checked={isChecked}
                  onChange={(e) => setIsChecked(e.target.checked)}
                  className="sr-only"
                />
                <label
                  htmlFor="terms"
                  className="flex items-center cursor-pointer"
                >
                  <div className={`w-4 h-4 border border-gray-300 dark:border-gray-600 rounded flex items-center justify-center transition-all duration-200 ${
                    isChecked
                      ? 'bg-gray-900 dark:bg-gray-100 border-gray-900 dark:border-gray-100'
                      : 'bg-white dark:bg-black hover:border-gray-400 dark:hover:border-gray-500'
                  }`}>
                    {isChecked && (
                      <CheckIcon />
                    )}
                  </div>
                </label>
              </div>
              <label htmlFor="terms" className="text-sm text-gray-600 dark:text-gray-400 cursor-pointer leading-4">
                I agree to the{' '}
                <a href="#" className="text-gray-900 dark:text-gray-100 hover:underline">
                  Terms of Service
                </a>{' '}
                and{' '}
                <a href="#" className="text-gray-900 dark:text-gray-100 hover:underline">
                  Privacy Policy
                </a>
              </label>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              className="signin-button w-full bg-gray-900 dark:bg-gray-100 text-white dark:text-gray-900 py-2 px-4 rounded-md text-sm font-medium hover:bg-gray-800 dark:hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-900 dark:focus:ring-gray-100 focus:ring-offset-2 focus:ring-offset-white dark:focus:ring-offset-black transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
              disabled={!isChecked}
            >
              Create account
            </button>
          </form>

          {/* Divider */}
          <div className="relative my-6">
            <div className="absolute inset-0 flex items-center">
              <div className="w-full border-t border-gray-200 dark:border-gray-800"></div>
            </div>
            <div className="relative flex justify-center text-xs uppercase">
              <span className="bg-white dark:bg-black px-2 text-gray-500 dark:text-gray-400">
                Or continue with
              </span>
            </div>
          </div>

          {/* Social Login */}
          <div className="grid grid-cols-2 gap-3">
            <button className="flex items-center justify-center py-2 px-3 border border-gray-200 dark:border-gray-800 bg-white dark:bg-black text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-900 transition-all duration-200 text-sm font-medium rounded-md">
              <TwitterIcon />
              <span className="ml-2">Twitter</span>
            </button>
            <button className="flex items-center justify-center py-2 px-3 border border-gray-200 dark:border-gray-800 bg-white dark:bg-black text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-900 transition-all duration-200 text-sm font-medium rounded-md">
              <LinkedInIcon />
              <span className="ml-2">LinkedIn</span>
            </button>
          </div>

          {/* Footer */}
          <div className="mt-6 text-center">
            <p className="text-sm text-gray-600 dark:text-gray-400">
              Already have an account?{' '}
              <a href="#" className="text-gray-900 dark:text-gray-100 font-medium hover:underline">
                Sign in
              </a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signin3;

